mapper.update:
UPDATE member
SET delete_status = '2'
WHERE id = #{id}

mapper.select:
SELECT w.id, w.serial_number, date_format(w.create_time, '%Y.%m.%d %T') createTime, date_format(w.update_time, '%Y.%m.%d %T') updateTime, w.age, w.birth_location, w.work_location, w.birth_year, w.height, w.weight, w.sex, w.job, w.marriage, w.remark, w.standard, w.educational_background, w.wechat_name FROM member w
and member.delete_status = '1'
and member.serial_number like #{serial_number}
and member.sex like #{sex}
ORDER BY w.id DESC LIMIT #{offSet}, #{pageRow}
